// `https://api.frankfurter.app/latest?amount=100&from=EUR&to=USD`

import { useState } from "react";

export default function App() {
  const [money, setMoney] = useState("");
  const [value, setValue] = useState("USD");
  const [value2, setValue2] = useState("EUR");
  function handleValue(e) {
    setMoney(e.target.value);
  }
  const api = `https://api.frankfurter.app/latest?amount=100&from=EUR&to=USD`;

  async function theapi() {
    const xxx = await fetch(api);
    const data = await xxx.json();
    // console.log(data.rates);
  }
  theapi();

  function handlefirst(e) {
    setValue(e.target.value);
  }
  function handlesecond(e) {
    setValue2(e.target.value);
  }
  function handlevalue(e) {
    setMoney(Number(e.target.value));
  }
  return (
    <div>
      <input type="text" onChange={handlevalue} />

      <select onChange={handlefirst}>
        <option value="USD">USD</option>
        <option value="EUR">EUR</option>
        <option value="CAD">CAD</option>
        <option value="INR">INR</option>
      </select>
      <select onChange={handlesecond}>
        <option value="USD">USD</option>
        <option value="EUR">EUR</option>
        <option value="CAD">CAD</option>
        <option value="INR">INR</option>
      </select>
      <p>{value} OUTPUT1</p>
      <p>{value2} OUTPUT2</p>
    </div>
  );
}
