// `https://api.frankfurter.app/latest?amount=100&from=EUR&to=USD`

import { useEffect, useState } from "react";

export default function App() {
  const [money, setMoney] = useState(0);
  const [value, setValue] = useState("USD");
  const [value2, setValue2] = useState("EUR");
  const [last, setLast] = useState("");
  function handlefirst(e) {
    setValue(e.target.value);
  }
  function handlesecond(e) {
    setValue2(e.target.value);
  }
  function handlevalue(e) {
    setLast(Number(e.target.value));
  }
  useEffect(
    function () {
      const api = `https://api.frankfurter.app/latest?amount=${last}&from=${value}&to=${value2}`;

      async function theapi() {
        const xxx = await fetch(api);
        const data = await xxx.json();

        console.log(data.rates);
        setLast(data.rates);
      }
      theapi();
    },
    [value, last, value2]
  );

  return (
    <div>
      <input type="text" onChange={handlevalue} />

      <select onChange={handlefirst} value={value}>
        <option value="USD">USD</option>
        <option value="EUR">EUR</option>
        <option value="CAD">CAD</option>
        <option value="INR">INR</option>
      </select>
      <select onChange={handlesecond} value={value2}>
        <option value="USD">USD</option>
        <option value="EUR">EUR</option>
        <option value="CAD">CAD</option>
        <option value="INR">INR</option>
      </select>
      {last.map((EUR) => (
        <p>{EUR.value2}</p>
      ))}
      <p> OUTPUT1</p>
    </div>
  );
}
