import { useState } from "react";

export default function App() {
  const [money, setMoney] = useState(0);
  const [value, setValue] = useState("");
  const [value2, setValue2] = useState("");
  const [last, setLast] = useState("");
  function handlefirst(e) {
    setValue(e.target.value);
  }
  function handlesecond(e) {
    setValue2(e.target.value);
  }
  // function handlevalue(e) {
  //   setLast(Number(e.target.value));
  // }
  const api = `https://api.frankfurter.app/latest?amount=${money}&from=${value}&to=${value2}`;

  async function theapi() {
    const xxx = await fetch(api);
    const data = await xxx.json();
    console.log(data.rates);
    setLast(data.rates);
  }
  theapi();

  return (
    <div>
      <input type="text" onChange={handlevalue} />

      <select onChange={handlefirst}>
        <option value="USD">USD</option>
        <option value="EUR">EUR</option>
        <option value="CAD">CAD</option>
        <option value="INR">INR</option>
      </select>
      <select onChange={handlesecond}>
        <option value="USD">USD</option>
        <option value="EUR">EUR</option>
        <option value="CAD">CAD</option>
        <option value="INR">INR</option>
      </select>
      <p>{last} OUTPUT1</p>
    </div>
  );
}
