import { useEffect, useState } from "react";

export default function App() {
  const [money, setMoney] = useState(0);
  const [value, setValue] = useState("USD");
  const [value2, setValue2] = useState("EUR");
  const [last, setLast] = useState(0);
  function handlefirst(e) {
    setValue(e.target.value);
  }
  function handlesecond(e) {
    setValue2(e.target.value);
  }
  function handlevalue(e) {
    setLast(e.target.value);
  }
  const theapi = `https://api.frankfurter.app/latest?amount=${last}&from=${value}&to=${value2}`;

  async function func() {
    const site = await fetch(theapi);
    const data = await site.json();
    console.log(data.rates);
    console.log(Object.values(data.rates));

    console.log(
      Object.keys(data).map(function (key) {
        return a[key];
      })
    );
    // setMoney(data.rates);
  }
  func();
  return (
    <div>
      <input type="text" onChange={handlevalue} />

      <select onChange={handlefirst} value={value}>
        <option value="USD">USD</option>
        <option value="EUR">EUR</option>
        <option value="CAD">CAD</option>
        <option value="INR">INR</option>
      </select>
      <select onChange={handlesecond} value={value2}>
        <option value="USD">USD</option>
        <option value="EUR">EUR</option>
        <option value="CAD">CAD</option>
        <option value="INR">INR</option>
      </select>

      <p>{money} OUTPUT1</p>
    </div>
  );
}
